LEFT JOIN j_admin_shop jas ON jas.shopId = jos.shopId
WHERE jos.scheduleDate BETWEEN #{scheduleReq.startDate} AND #{scheduleReq.endDate} AND jos.isDel=16 AND jc.customerPhone LIKE concat(concat('%',#{scheduleReq.customerPhone}),'%') AND jc.customerName LIKE concat(concat('%',#{scheduleReq.customerName}),'%') AND jch.houseAddress LIKE concat(concat('%',#{scheduleReq.customerAddress}),'%') AND jni.nannyName LIKE concat(concat('%',#{scheduleReq.nannyName}),'%') AND jni.nannyPhone LIKE concat(concat('%',#{scheduleReq.nannyPhone}),'%')
