AND jcd.companyName LIKE concat(concat('%',#{scheduleReq.nannyName}),'%') AND js.phone LIKE concat(concat('%',#{scheduleReq.nannyPhone}),'%')
AND jot.customerId = #{scheduleReq.customerId}
