SELECT jc.customerId, jc.customerName, jc.customerPhone, js.shopId, js.shopName, jc.customerBalance, j.scheduleDate, SUM(CASE j.totalPrice WHEN 0 THEN CONVERT((j.endTime - j.startTime) / 2 * jgc.price,DECIMAL) ELSE j.totalPrice END) AS totalPrice FROM j_order_schedule j LEFT JOIN j_order_contract joc ON joc.orderId = j.orderId LEFT JOIN j_customer jc ON jc.customerId = joc.customerId LEFT JOIN j_shop js ON js.shopId = joc.shopId LEFT JOIN j_goods_contract jgc ON jgc.goodsId = joc.goodsId
LEFT JOIN j_admin_shop jas ON jas.shopId = joc.shopId
WHERE j.payStatus = 157 AND j.isDel=16 AND j.orderType = 163 AND j.scheduleDate BETWEEN #{scheduleReq.startDate} AND #{scheduleReq.endDate}
AND joc.customerId = #{scheduleReq.customerId}
AND jc.customerPhone LIKE concat(concat('%',#{scheduleReq.customerPhone}),'%') AND jc.customerName LIKE concat(concat('%',#{scheduleReq.customerName}),'%')
AND joc.shopId = #{scheduleReq.shopId}
AND jas.adminId = #{scheduleReq.adminId}
GROUP BY joc.customerId ORDER BY j.scheduleDate LIMIT #{scheduleReq.page},#{scheduleReq.size}

