LEFT JOIN j_admin_shop jas ON jas.shopId = joc.shopId
WHERE j.payStatus = 157 AND j.isDel=16 AND j.orderType = 163 AND j.scheduleDate BETWEEN #{scheduleReq.startDate} AND #{scheduleReq.endDate}
AND joc.customerId = #{scheduleReq.customerId}
AND jc.customerPhone LIKE concat(concat('%',#{scheduleReq.customerPhone}),'%') AND jc.customerName LIKE concat(concat('%',#{scheduleReq.customerName}),'%')
AND joc.shopId = #{scheduleReq.shopId}
AND jas.adminId = #{scheduleReq.adminId}

