mapper.select:
SELECT j.*,jdi.INFO AS couponOriginInfo FROM j_coupon j LEFT JOIN j_dict_info jdi ON jdi.ID = j.couponOrigin WHERE j.isDel=16
and j.couponOrigin = #{couponReq.couponOrigin}
AND j.couponName LIKE concat("%",concat(#{couponReq.couponName},"%")) limit #{couponReq.page},#{couponReq.size}

SELECT j.*,jdi.INFO AS couponOriginInfo FROM j_coupon j LEFT JOIN j_dict_info jdi ON jdi.ID = j.couponOrigin WHERE j.isDel=16
and j.couponOrigin = #{couponReq.couponOrigin}
AND j.isDel=16 AND j.validTime >= CURRENT_DATE limit #{couponReq.page},#{couponReq.size}
