mapper.select:
SELECT
COUNT(DISTINCT a.customerId)
FROM j_customer a
LEFT JOIN j_customer_house jch ON a.customerId=jch.customerId

    LEFT JOIN j_admin_shop jas ON jas.shopId = jch.shopId

WHERE a.isDel = 16

    AND jas.adminId = #{customerPagReq.adminId}


    AND a.customerId = #{customerPagReq.customerId}


    AND a.customerType = #{customerPagReq.customerType}


    AND jch.shopId = #{customerPagReq.shopId}


    AND a.customerPhone LIKE concat(concat('%',#{customerPagReq.customerPhone}),'%')



    AND a.customerName LIKE concat(concat('%',#{customerPagReq.customerName}),'%')

SELECT COUNT(DISTINCT a.customerId) FROM j_customer a LEFT JOIN j_dict_info d1 ON a.customerType = d1.ID LEFT JOIN j_dict_info d3 ON a.customerSex = d3.ID LEFT JOIN j_dict_info d4 ON a.warnType = d4.ID LEFT JOIN j_dict_info d5 ON a.isDel = d5.ID WHERE a.isDel = 16 AND a.customerId NOT IN ( SELECT j.customerId FROM j_customer j INNER JOIN j_customer_house jch ON jch.customerId = j.customerId AND j.isDel = 16 )
AND a.customerId = #{customerPagReq.customerId}
AND a.customerType = #{customerPagReq.customerType}
AND a.customerPhone LIKE concat(concat('%',#{customerPagReq.customerPhone}),'%')
AND a.customerName LIKE concat(concat('%',#{customerPagReq.customerName}),'%')
ORDER BY a.createTime

SELECT a.*,d1.INFO AS customerTypeInfo,d3.INFO AS customerSexInfo,d4.INFO AS warnTypeInfo,d5.INFO AS isDelInfo FROM j_customer a LEFT JOIN j_customer_house jch ON a.customerId=jch.customerId
LEFT JOIN j_admin_shop jas ON jas.shopId = jch.shopId
LEFT JOIN j_dict_info d1 ON a.customerType = d1.ID LEFT JOIN j_dict_info d3 ON a.customerSex = d3.ID LEFT JOIN j_dict_info d4 ON a.warnType = d4.ID LEFT JOIN j_dict_info d5 ON a.isDel = d5.ID WHERE a.isDel = 16
AND jas.adminId = #{customerPagReq.adminId}
AND a.customerId = #{customerPagReq.customerId}
AND a.customerType = #{customerPagReq.customerType}
AND jch.shopId = #{customerPagReq.shopId}
AND a.customerPhone LIKE concat(concat('%',#{customerPagReq.customerPhone}),'%')
AND a.customerName LIKE concat(concat('%',#{customerPagReq.customerName}),'%')
GROUP BY a.customerId ORDER BY a.createTime LIMIT #{customerPagReq.page},#{customerPagReq.size}

SELECT a.*,d1.INFO AS customerTypeInfo,d3.INFO AS customerSexInfo,d4.INFO AS warnTypeInfo,d5.INFO AS isDelInfo FROM j_customer a LEFT JOIN j_dict_info d1 ON a.customerType = d1.ID LEFT JOIN j_dict_info d3 ON a.customerSex = d3.ID LEFT JOIN j_dict_info d4 ON a.warnType = d4.ID LEFT JOIN j_dict_info d5 ON a.isDel = d5.ID WHERE a.isDel = 16 AND a.customerId NOT IN ( SELECT j.customerId FROM j_customer j INNER JOIN j_customer_house jch ON jch.customerId = j.customerId AND j.isDel = 16 )
AND a.customerId = #{customerPagReq.customerId}
AND a.customerType = #{customerPagReq.customerType}
AND a.customerPhone LIKE concat(concat('%',#{customerPagReq.customerPhone}),'%')
AND a.customerName LIKE concat(concat('%',#{customerPagReq.customerName}),'%')
ORDER BY a.createTime LIMIT #{customerPagReq.page},#{customerPagReq.size}

