mapper.select:
SELECT
COUNT(*)
FROM j_customer_statment
WHERE 1=1
AND isDel=16

    AND customerId = #{customerStatmentReq.customerId}


    AND statmentCustomerType = #{customerStatmentReq.statmentCustomerType}


    AND status = #{customerStatmentReq.status}

SELECT a.*,d2.INFO AS statmentCustomerTypeInfo,d3.INFO AS chargeWayInfo,d4.INFO AS terminalInfo,d5.INFO AS statusInfo,d6.customerName AS customerName,d6.customerPhone, d7.adminName AS adminName,jch.houseAddress,jc.couponName,jc.couponPrice,jgc.service AS goodsName FROM j_customer_statment a LEFT JOIN j_dict_info d2 ON d2.ID = a.statmentCustomerType LEFT JOIN j_dict_info d3 ON d3.ID = a.chargeWay LEFT JOIN j_dict_info d4 ON d4.ID = a.terminal LEFT JOIN j_dict_info d5 ON d5.ID = a.status LEFT JOIN j_customer d6 ON d6.customerId = a.customerId LEFT JOIN j_admin d7 ON d7.adminId = a.adminId LEFT JOIN j_customer_house jch ON jch.houseId=a.houseId LEFT JOIN j_coupon_list jcl ON jcl.couponListId=a.couponListId LEFT JOIN j_coupon jc ON jc.couponId=jcl.couponId LEFT JOIN j_goods_contract jgc ON jgc.goodsId=a.goodsId WHERE 1=1 AND a.isDel=16
AND a.customerId = #{customerStatmentReq.customerId}
AND a.statmentCustomerType = #{customerStatmentReq.statmentCustomerType}
AND a.status = #{customerStatmentReq.status}
ORDER BY a.chargeDate LIMIT #{customerStatmentReq.page},#{customerStatmentReq.size}

SELECT SUM(chargeMoney)
FROM j_customer_statment
WHERE customerId=#{customerId}
AND chargeDate BETWEEN #{startDate} AND #{endDate}
and statmentCustomerType=38
AND isDel=16

SELECT j.shopId, js.shopName, SUM( CASE j.chargeWay WHEN 44 THEN j.chargeMoney ELSE 0 END ) AS 'aliPay', SUM( CASE j.chargeWay WHEN 45 THEN j.chargeMoney ELSE 0 END ) AS 'WeiXin', SUM( CASE j.chargeWay WHEN 127 THEN j.chargeMoney ELSE 0 END ) AS 'cash', SUM( CASE j.chargeWay WHEN 209 THEN j.chargeMoney ELSE 0 END ) AS 'card', SUM(j.chargeMoney) AS 'totalCharge', SUM(jc.customerBalance) AS totalBalance FROM j_customer_statment j LEFT JOIN j_customer_house jch ON jch.customerId=j.customerId LEFT JOIN j_shop js ON js.shopId = j.shopId LEFT JOIN j_customer jc ON jc.customerId = j.customerId WHERE j.chargeWay IN (44,45,127,209,243) AND j.isDel=16 AND j.chargeDate BETWEEN #{map.startDate} AND #{map.endDate} AND jch.shopId IN
#{item}
ORDER BY j.chargeDate

SELECT a.*,d2.INFO AS statmentCustomerTypeInfo,d3.INFO AS chargeWayInfo,d4.INFO AS terminalInfo,d5.INFO AS statusInfo,d6.customerName AS customerName,d6.customerPhone,d7.adminName AS adminName,js.shopName FROM j_customer_statment a LEFT JOIN j_dict_info d2 ON d2.ID = a.statmentCustomerType LEFT JOIN j_dict_info d3 ON d3.ID = a.chargeWay LEFT JOIN j_dict_info d4 ON d4.ID = a.terminal LEFT JOIN j_dict_info d5 ON d5.ID = a.status LEFT JOIN j_customer d6 ON d6.customerId = a.customerId LEFT JOIN j_admin d7 ON d7.adminId = a.adminId LEFT JOIN j_shop js ON js.shopId=a.shopId WHERE a.statmentCustomerType = 38 AND a.isDel=16 AND d6.customerName LIKE concat(concat('%',#{map.customerName}),'%') AND d6.customerPhone LIKE concat(concat('%',#{map.customerPhone}),'%') AND a.serialNumber LIKE concat(concat('%',#{map.serialNumber}),'%') AND a.chargeDate BETWEEN #{map.startDate} AND #{map.endDate}
AND a.customerId = #{map.customerId}
AND a.shopId = #{map.shopId} AND a.chargeWay IN
#{item}
AND a.terminal IN
#{item}
ORDER BY a.chargeDate LIMIT #{map.page},#{map.size}

SELECT COUNT(a.statmentId) FROM j_customer_statment a LEFT JOIN j_dict_info d2 ON d2.ID = a.statmentCustomerType LEFT JOIN j_dict_info d3 ON d3.ID = a.chargeWay LEFT JOIN j_dict_info d4 ON d4.ID = a.terminal LEFT JOIN j_dict_info d5 ON d5.ID = a.status LEFT JOIN j_customer d6 ON d6.customerId = a.customerId LEFT JOIN j_admin d7 ON d7.adminId = a.adminId LEFT JOIN j_shop js ON js.shopId=a.shopId
LEFT JOIN j_admin_shop jas2 ON jas2.shopId=a.shopId
WHERE a.statmentCustomerType = 38 AND a.isDel=16 AND d6.customerName LIKE concat(concat('%',#{map.customerName}),'%') AND d6.customerPhone LIKE concat(concat('%',#{map.customerPhone}),'%') AND a.serialNumber LIKE concat(concat('%',#{map.serialNumber}),'%') AND a.chargeDate BETWEEN #{map.startDate} AND #{map.endDate}
AND a.customerId = #{map.customerId}
AND a.shopId = #{map.shopId} AND a.chargeWay IN
#{item}
AND a.terminal IN
#{item}
